// src/components/reservation/ClientForm.js
import React from 'react';

const ClientForm = ({ client, onChange }) => {
  const handleChange = (field, value) => {
    onChange({ ...client, [field]: value });
  };

  return (
    <div className="client-form">
      <div className="form-section">
        <h3>Informations personnelles</h3>
        
        <div className="form-row">
          <div className="form-group">
            <label>Pr√©nom *</label>
            <input
              type="text"
              value={client.prenom}
              onChange={(e) => handleChange('prenom', e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Nom *</label>
            <input
              type="text"
              value={client.nom}
              onChange={(e) => handleChange('nom', e.target.value)}
              required
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              value={client.email}
              onChange={(e) => handleChange('email', e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label>T√©l√©phone *</label>
            <input
              type="tel"
              value={client.telephone}
              onChange={(e) => handleChange('telephone', e.target.value)}
              placeholder="+223 XX XX XX XX"
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label>Pays</label>
          <select 
            value={client.pays} 
            onChange={(e) => handleChange('pays', e.target.value)}
          >
            <option value="Togo">Togo</option>
            <option value="B√©nin">B√©nin</option>
            <option value="Ghana">Ghana</option>
            <option value="C√¥te d'Ivoire">C√¥te d'Ivoire</option>
            <option value="Autre">Autre</option>
          </select>
        </div>

        <div className="form-group">
          <label>Notes sp√©ciales (optionnel)</label>
          <textarea
            value={client.notes}
            onChange={(e) => handleChange('notes', e.target.value)}
            placeholder="Demandes sp√©ciales, allergies, pr√©f√©rences..."
            rows="3"
          />
        </div>
      </div>

      <div className="form-notice">
        <p>üìß Vous recevrez une confirmation par email</p>
        <p>üìû Notre √©quipe peut vous contacter pour confirmer votre r√©servation</p>
      </div>
    </div>
  );
};

export default ClientForm;
