import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./HotelRoomsPage.css";

const HotelRoomsPage = () => {
  const { id } = useParams(); // ID de l'hôtel
  const [rooms, setRooms] = useState([]);
  const [hotelName, setHotelName] = useState("");
  const [selectedRoom, setSelectedRoom] = useState(null);

  useEffect(() => {
  const fetchRooms = () => {
    axios.get(`http://localhost:5000/api/hotels/${id}/rooms`)
      .then(res => setRooms(res.data))
      .catch(err => console.error("Erreur chargement chambres", err));
  };

  // Charger le nom de l'hôtel une seule fois
  axios.get(`http://localhost:5000/api/hotels/${id}`)
    .then(res => setHotelName(res.data.name))
    .catch(err => console.error("Erreur chargement hôtel", err));

  fetchRooms(); // chargement initial
  const interval = setInterval(fetchRooms, 30000); // toutes les 30s

  return () => clearInterval(interval); // nettoyage
}, [id]);


  const handleReservation = async (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const data = Object.fromEntries(form.entries());
    data.room_id = selectedRoom.id;

    try {
      await axios.post('http://localhost:5000/api/reservations', data);
      alert('Réservation confirmée');
      setSelectedRoom(null);
      fetchRooms(); // 🔄 Mise à jour des disponibilités
    } catch (err) {
      console.error('Erreur réservation', err);
    }
  };

  return (
    <div className="hotel-rooms-page container py-5">
      <h2 className="mb-4">Chambres disponibles pour : {hotelName}</h2>
      <Link to="/" className="btn btn-secondary mb-4">← Retour aux hôtels</Link>

      {rooms.length === 0 ? (
        <p>Aucune chambre disponible pour cet hôtel.</p>
      ) : (
        <div className="row">
          {rooms.map(room => (
            <div
              key={room.id}
              className="col-md-4 mb-4"
              onClick={() => setSelectedRoom(room)}
              style={{ cursor: "pointer" }}
            >
              <div className="card h-100 shadow-sm">
                {room.image && (
                  <img
                    src={`http://localhost:5000/${room.image}`}
                    alt={room.type}
                    className="card-img-top room-image"
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">{room.type}</h5>
                  <p className="card-text">{room.description}</p>
                  <p><strong>Prix:</strong> {room.price} €</p>
                  <p><strong>Capacité:</strong> {room.capacity} personnes</p>
                  <p><strong>Commodités:</strong> {room.amenities}</p>
                  <p><strong>Disponibilité:</strong> {room.availability}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedRoom && (
        <div className="reservation-form mt-5">
          <h4>Réserver la chambre : {selectedRoom.type}</h4>
          <form onSubmit={handleReservation} className="row g-3">
            <div className="col-md-6">
              <input type="text" name="client_name" className="form-control" placeholder="Nom complet" required />
            </div>
            <div className="col-md-6">
              <input type="email" name="client_email" className="form-control" placeholder="Email" required />
            </div>
            <div className="col-md-6">
              <label>Date d'arrivée</label>
              <input type="date" name="check_in" className="form-control" required />
            </div>
            <div className="col-md-6">
              <label>Date de départ</label>
              <input type="date" name="check_out" className="form-control" required />
            </div>
            <div className="col-12">
              <button type="submit" className="btn btn-success me-2">Confirmer la réservation</button>
              <button type="button" className="btn btn-outline-secondary" onClick={() => setSelectedRoom(null)}>Annuler</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default HotelRoomsPage;
