        onClick={() => setActiveTab('stats')}
      >
        Statistiques
      </button>
      <button
        className={activeTab === 'rooms' ? 'active' : ''}
        onClick={() => setActiveTab('rooms')}
      >
        Chambres
      </button>
      <button
        className={activeTab === 'hotels' ? 'active' : ''}
        onClick={() => setActiveTab('hotels')}
