import { useState, useEffect } from 'react';
import axios from 'axios';

export default function RoomManager() {
  const [chambres, setChambres] = useState([]);
  const [form, setForm] = useState({ nom: '', description: '', prix: '', image: null, hotel_id: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/chambres').then(res => setChambres(res.data));
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleFile = e => setForm({ ...form, image: e.target.files[0] });

  const handleSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    Object.entries(form).forEach(([key, val]) => data.append(key, val));

    if (editingId) {
      await axios.put(`http://localhost:5000/api/chambres/${editingId}`, data);
    } else {
      await axios.post('http://localhost:5000/api/chambres', data);
    }

    setForm({ nom: '', description: '', prix: '', image: null, hotel_id: '' });
    setEditingId(null);
    const res = await axios.get('http://localhost:5000/api/chambres');
    setChambres(res.data);
  };

  const handleEdit = chambre => {
    setForm({ ...chambre, image: null });
    setEditingId(chambre.id);
  };

  const handleDelete = async id => {
    await axios.delete(`http://localhost:5000/api/chambres/${id}`);
    setChambres(chambres.filter(c => c.id !== id));
  };

  return (
    <div>
      <h2>{editingId ? 'Modifier' : 'Ajouter'} une chambre</h2>
      <form onSubmit={handleSubmit}>
        <input name="nom" value={form.nom} onChange={handleChange} placeholder="Nom" required />
        <textarea name="description" value={form.description} onChange={handleChange} placeholder="Description" required />
        <input name="prix" type="number" value={form.prix} onChange={handleChange} placeholder="Prix" required />
        <input name="hotel_id" value={form.hotel_id} onChange={handleChange} placeholder="ID Hôtel" required />
        <input type="file" accept="image/*" onChange={handleFile} />
        <button type="submit">{editingId ? 'Modifier' : 'Ajouter'}</button>
      </form>

      <h3>Liste des chambres</h3>
      <ul>
        {chambres.map(c => (
          <li key={c.id}>
            <strong>{c.nom}</strong> - {c.prix}€
            {c.image && <img src={`http://localhost:3001/uploads/chambres/${c.image}`} width="100" />}
            <button onClick={() => handleEdit(c)}>Modifier</button>
            <button onClick={() => handleDelete(c.id)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
