import React, { useState, useEffect } from 'react';
import axios from 'axios';
import HotelManager from '../components/HotelManager';
import RoomManager from '../components/RoomManager';
import AvailabilityUpdater from '../components/AvailabilityUpdater';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('hotels');
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    if (activeTab === 'reservations') {
      axios.get('http://localhost:5000/api/reservations')
        .then(res => setReservations(res.data))
        .catch(err => console.error('Erreur chargement r√©servations', err));
    }
  }, [activeTab]);

  const renderTab = () => {
    switch (activeTab) {
      case 'hotels':
        return <HotelManager />;
      case 'rooms':
        return <RoomManager />;
      case 'availability':
        return <AvailabilityUpdater />;
      case 'reservations':
        return (
          <div className="reservation-list">
            <h3>üìã R√©servations</h3>
            {reservations.length === 0 ? (
              <p>Aucune r√©servation enregistr√©e.</p>
            ) : (
              <table className="table table-bordered">
                <thead className="table-light">
                  <tr>
                    <th>Client</th>
                    <th>Email</th>
                    <th>Chambre</th>
                    <th>Check-in</th>
                    <th>Check-out</th>
                  </tr>
                </thead>
                <tbody>
                  {reservations.map(r => (
                    <tr key={r.id}>
                      <td>{r.client_name}</td>
                      <td>{r.client_email}</td>
                      <td>{r.room_id}</td>
                      <td>{r.check_in}</td>
                      <td>{r.check_out}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        );
      default:
        return <HotelManager />;
    }
  };

  return (
    <div className="admin-dashboard container py-4">
      <h1 className="mb-4">Interface Admin ‚Äì Gestion H√¥teli√®re</h1>

      <nav className="mb-4">
        <button onClick={() => setActiveTab('hotels')} className="btn btn-outline-primary me-2">
          üè® H√¥tels
        </button>
        <button onClick={() => setActiveTab('rooms')} className="btn btn-outline-primary me-2">
          üõèÔ∏è Chambres
        </button>
        <button onClick={() => setActiveTab('availability')} className="btn btn-outline-primary me-2">
          üìä Disponibilit√©
        </button>
        <button onClick={() => setActiveTab('reservations')} className="btn btn-outline-primary">
          üìã R√©servations
        </button>
      </nav>

      <div className="admin-content">
        {renderTab()}
      </div>
    </div>
  );
};

export default AdminDashboard;
