import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HotelStats = ({ hotelId }) => {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    if (!hotelId) return;
    axios
      .get(`/api/stats/${hotelId}`)
      .then((res) => setStats(res.data))
      .catch((err) => console.error('Erreur stats:', err));
  }, [hotelId]);

  if (!stats) return <p>Chargement des statistiques...</p>;

  return (
    <div>
      <h3>Statistiques de l’hôtel</h3>
      <p>Réservations totales : {stats.totalReservations}</p>
      <p>Chambres disponibles : {stats.availableRooms}</p>
    </div>
  );
};

export default HotelStats;
