import React, { useState } from 'react';
import HotelManager from '../admin/HotelManager';
import RoomManager from '../admin/RoomManager';
import AvailabilityUpdater from '../admin/AvailabilityUpdater';


const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('hotels');

  const renderTab = () => {
    switch (activeTab) {
      case 'hotels':
        return <HotelManager />;
      case 'rooms':
        return <RoomManager />;
      case 'availability':
        return <AvailabilityUpdater />;
      default:
        return <HotelManager />;
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Interface Admin ‚Äì Gestion H√¥teli√®re</h1>

      <nav style={{ marginBottom: '20px' }}>
        <button onClick={() => setActiveTab('hotels')} style={{ marginRight: '10px' }}>
          üè® H√¥tels
        </button>
        <button onClick={() => setActiveTab('rooms')} style={{ marginRight: '10px' }}>
          üõèÔ∏è Chambres
        </button>
        <button onClick={() => setActiveTab('availability')}>
          üìä Disponibilit√©
        </button>
      </nav>

      <div className="admin-content">
        {renderTab()}
      </div>
    </div>
  );
};

export default AdminDashboard;
